 and isdel=#{isdel,jdbcType=VARCHAR}

 select   

,

  from forder where 1=1

  and uid=#{uid,jdbcType=INTEGER}


  and fid=#{fid,jdbcType=INTEGER}


  and status like  concat('%',#{status,jdbcType=VARCHAR},'%')


  and isdel=#{isdel,jdbcType=VARCHAR}

   select 

,

  from forder  where 1=1


   and uid=#{uid,jdbcType=INTEGER}


   and fid=#{fid,jdbcType=INTEGER}


    and status like  concat('%',#{status,jdbcType=VARCHAR},'%')


   and isdel=#{isdel,jdbcType=VARCHAR}


